import React, {useState} from 'react';
import Form from './Form';
import './App.css';
import Modal from './Modal';

function App() {
  const [data, setData] = useState([]);
  const getDataHandler = (event) =>{
    setData([...data, event])
  }
  return (
    <div>
      <Form getdata={getDataHandler}/>
      <Modal data={data}/>
    </div>
  );
}

export default App;
